// import React from 'react'
// import {motion} from "framer-motion";
import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
// const animations = {
//     initial: {x:1000,opacity:0},
//     animate: {x:0,opacity:1, transition: {duration: 1, ease: "easeInOut"}},
// }

function PageTransition({children}) {
  // return (
  // <>
  //   <motion.div variants={animations}
  //     initial="initial" 
  //     animate="animate" 
  //     exit="exit">
  //         {children}
  //   </motion.div>
  // </>
  // )
   const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    setIsLoaded(true);
  }, []);

  const warpVariants = {
    initial: {
      scale: 0,
      opacity: 0,
      transition: {
        duration: 1.5,
        ease: 'easeInOut',
      },
    },
    animate: {
      scale: 1,
      opacity: 1,
      transition: {
        duration: 1.5,
        ease: 'easeInOut',
      },
    },
    exit: {
      scale: 2,
      opacity: 0,
      transition: {
        duration: 1.5,
        ease: 'easeInOut',
      },
    },
  };

  return (
    <>
      {isLoaded && (
        <motion.div
          className="warp-transition"
          initial="initial"
          animate="animate"
          exit="exit"
          variants={warpVariants}
        >
          <motion.div className="warp-circle" />
          <motion.div className="warp-circle" />
          <motion.div className="warp-circle" />
          <motion.div className="warp-circle" />
        </motion.div>
      )}
      <div className="page-content">{children}</div>
    </>
  );


}

export default PageTransition
